<?php

namespace AdminBundle\Entity;

use Doctrine\ORM\Tools\Pagination\Paginator;

/**
 * UserRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class UserRepository extends \Doctrine\ORM\EntityRepository
{
    /**
     * @param int $record
     * @return array|null
     * @throws \Doctrine\ORM\NonUniqueResultException
     */
    public function findOneUser($record)
    {
        return $this->getEntityManager()
            ->createQuery("
                SELECT u.id,
                       u.username,
                       u.email,
                       u.isActive,
                       u.deleted,
                       r.id AS role_id
                  FROM AdminBundle:User u LEFT JOIN u.roles r
                 WHERE u.id = :id
            ")
            ->setParameter('id', $record)
            ->setMaxResults(1)
            ->getOneOrNullResult();
    }

    /**
     * @param int $deleted
     * @return array
     */
    public function findListUser($page = 1, $deleted = 0, $max = 50)
    {
        $query = $this->getEntityManager()
            ->createQuery("
                SELECT u.id,
                       u.username,
                       u.email,
                       u.isActive
                  FROM AdminBundle:User u
                 WHERE u.deleted = :deleted
            ")
            ->setParameter('deleted', $deleted)
            ->setFirstResult($page < 1 ? 1: $page - 1)
            ->setMaxResults($max);

        $paginator = new Paginator($query);

        $paginator->setUseOutputWalkers(false);

        return [
            'count_page' => ceil($paginator->count() / $max),
            'list'  => $query->getResult()
        ];
    }
}
